import React, {useState} from 'react';

const EditUserPopup = ({isVisible, onClick}) => {
    const [cardName, setCardName] = useState('')
    const [cardLink, setCardLink] = useState('')

    const handleSubmit = () => {
        console.log('gf')
    }

    return (
        <section className={isVisible ? "popup popup_opened" : "popup"} onClick={() => {
            onClick();
            setCardName('')
            setCardLink('')
        }}>
            <div className='popup__container' onClick={e => e.stopPropagation()}>
                <button title='close-button' type="reset"
                        className='popup__close-button popup__close-button_parent-corners_straight'/>
                <h2 className="popup__title">Обновить профиль</h2>
                <form onSubmit={handleSubmit} className='form'>
                    <input className='form__input' placeholder='Изменить имя' value={cardLink}
                           onChange={(e) => setCardLink(e.target.value)}/>
                    <input className='form__input' placeholder='Изменить описание' value={cardName}
                           onChange={(e) => setCardName(e.target.value)}/>
                    <button
                        title="Обновить"
                        type="submit"
                        className='popup__button form__button'
                    >
                        Обновить
                    </button>
                </form>
            </div>
        </section>
    );
};

export default EditUserPopup;
