import React, {useState} from 'react';

const EditUserPhotoPopup = ({isVisible, onClick}) => {
    const [inputValue, setInputValue] = useState('')

    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(inputValue)
    }


    return (
        <section className={isVisible ? "popup popup_opened" : "popup"} onClick={() => {
            onClick();
            setInputValue('')
        }}>
            <div className='popup__container' onClick={e => e.stopPropagation()}>
                <button title='close-button' type="reset"
                        className='popup__close-button popup__close-button_parent-corners_straight'/>
                <h2 className="popup__title">Обновить аватар</h2>
                <form onSubmit={handleSubmit} className='form'>
                    <input className='form__input' placeholder='Ссылка на картинку' value={inputValue}
                           onChange={(e) => setInputValue(e.target.value)}/>
                    <button
                        title="Обновить"
                        type="submit"
                        className='popup__button form__button'
                    >
                        Обновить
                    </button>
                </form>
            </div>
        </section>
    );
};

export default EditUserPhotoPopup;
