import React, {useCallback, useState} from 'react';
import Profile from "../Profile/Profile";
import Cards from "../Cards/Cards";
import SingleCard from "../Cards/SingleCard";
import ImagePopup from "../ImagePopup/ImagePopup";

const Main = () => {
    const [isPopupOpened, setIsPopupOpened] = useState(false)
    const [selectedCard, setSelectedCard] = useState({})

    const cards = [
        {
            link: 'https://mobimg.b-cdn.net/v3/fetch/a5/a572284e28c5218a3ee631c937be827c.jpeg',
            name: 'Kitten',
            authorInfo: 'Jija negra'
        },
        {
            link: 'https://mobimg.b-cdn.net/v3/fetch/a5/a572284e28c5218a3ee631c937be827c.jpeg',
            name: 'Kitten',
            authorInfo: 'Jija negra'
        },
        {
            link: 'https://mobimg.b-cdn.net/v3/fetch/a5/a572284e28c5218a3ee631c937be827c.jpeg',
            name: 'Kitten',
            authorInfo: 'Jija negra'
        },
        {
            link: 'https://mobimg.b-cdn.net/v3/fetch/a5/a572284e28c5218a3ee631c937be827c.jpeg',
            name: 'Kitten',
            authorInfo: 'Jija negra'
        },
        {
            link: 'https://mobimg.b-cdn.net/v3/fetch/a5/a572284e28c5218a3ee631c937be827c.jpeg',
            name: 'Kitten',
            authorInfo: 'Jija negra'
        }

    ]

    const closeAllPopups = () => {
        setIsPopupOpened(!isPopupOpened)
    }

    const handleDeleteClick = (card) => {
        console.log('Удалил типо')
    }

    const handleOnCardClick = (card) => {
        setSelectedCard(card)
        setIsPopupOpened(true)
    }

    return (
        <>
            <main className="content">
                <Profile avatar='https://mobimg.b-cdn.net/v3/fetch/a5/a572284e28c5218a3ee631c937be827c.jpeg'
                         name='Даниил'
                         about='Drug addict'/>
                <Cards>
                    {cards.map((card, idx) => {
                        return (
                            <SingleCard name={card.name} link={card.link} authorInfo={card.authorInfo} key={idx}
                                        onCardClick={() => handleOnCardClick(card)}
                                        handleDeleteClick={() => handleDeleteClick(card)}/>
                        )
                    })}
                </Cards>
            </main>
            <ImagePopup isVisible={isPopupOpened} card={selectedCard}
                        onClick={closeAllPopups}/>
        </>
    );
};

export default Main;
