import React from 'react';

const ImagePopup = ({card, isVisible, handleClosePopup}) => {


    return (
        <figure className={isVisible ? "popup__figure popup_opened" : "popup__figure"}>
            <button title={card.name} type="button" className='popup__close-button' onClick={handleClosePopup}/>
            <img className="popup__image" alt={card.name} src={card.link}/>
            <figcaption className="popup__caption">{card.name}</figcaption>
        </figure>
    );
};

export default ImagePopup;
