import React from 'react'
import { useQuery } from 'react-query'
import { $api } from '../../api/api'
import EditUserPhotoPopup from '../Popups/EditUserPhotoPopup/EditUserPhotoPopup'
import EditUserPopup from '../Popups/EditUserPopup/EditUserPopup'

const Profile = ({
	onEditAvatar,
	onEditProfile,
	onAddPlace,
    isEditPhotoPopupOpened,
    closeAllPopups,
    isEditProfilePopupOpened
}) => {

    const logo = 'https://csgoluxe.ru/wp-content/uploads/2019/10/word-image.jpeg'

    const {data, refetch} = useQuery(
        ['Get Profile info'],
        () => 
        $api({
            url: '/users/profile'
        })
    )

	return (
		<>
			<section className="profile">
				<button
					onClick={onEditAvatar}
					type="button"
					className="profile__avatar-container"
				>
					<img alt="Аватар" className="profile__avatar" src={data.avatar ? data.avatar : logo } />
				</button>
				<div className="profile__info">
					<h1 className="profile__name">{data.name}</h1>
					<p className="profile__description">{data.description}</p>
					<button
						onClick={onEditProfile}
						type="button"
						className="profile__edit-button"
					/>
				</div>
				<button
					onClick={onAddPlace}
					type="button"
					className="profile__add-button"
				/>
			</section>
			<EditUserPhotoPopup
				isVisible={isEditPhotoPopupOpened}
				onClick={closeAllPopups}
                refetch={refetch}
			/>
			<EditUserPopup
				isVisible={isEditProfilePopupOpened}
				onClick={closeAllPopups}
                refetch={refetch}
			/>
		</>
	)
}

export default Profile
