import React from 'react';
import { useQuery } from 'react-query';
import { $api } from '../../api/api';
import SingleCard from './SingleCard';

const Cards = ({handleOnCardClick}) => {
    const {data, refetch, isSuccess} = useQuery(
        ["get cards"],
        () => 
          $api({
            url: `/cards`
          }),
          {
            refetchOnWindowFocus: false,
          }
    )
    return (
      <>
        <section className="elements">
            <ul className="elements__list">
            {isSuccess && 
            data.map((card, idx) => {
                        return (
                            <SingleCard name={card.name} url={card.url} key={idx}
                                        onCardClick={() => handleOnCardClick(card)}
                                         refetch={refetch} item={card._id}/>
                        )
                    })
            }
            </ul>
        </section>
                    <ImagePopup isVisible={isPopupOpened} card={selectedCard} onClick={closeAllPopups}/>
                    <EditUserPhotoPopup isVisible={isEditPhotoPopupOpened} onClick={closeAllPopups}/>
                    <EditUserPopup isVisible={isEditProfilePopupOpened} onClick={closeAllPopups}/>
                    <AddCardPopup isVisible={isAddCardPopupOpened} onClick={closeAllPopups}/>
      </>
    );
};

export default Cards;
